import Slider from "react-slick";

const ProjetItem = ({ titre, techno, description, imagesSrc, link }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  if (!imagesSrc) {
    return null;
  }

  return (
    <div className="projet">
      <h4>{titre}</h4>
      <h5>Technologies utilis√©es: {techno}</h5>
      <p>{description}</p>
      <Slider {...settings}>
        {imagesSrc.map((image, index) => (
          <div key={index}>
            <img src={image} alt={titre + " - Image " + (index + 1)} />
          </div>
        ))}
      </Slider>
      <p>
        <a href={link} target="_blank" rel="noreferrer">
          Lien vers le site de vente
        </a>
      </p>
    </div>
  );
};

export default ProjetItem;
