import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Projet = ({ titre, techno, description, imageSrc, link }) => {
  const [selectedImage, setSelectedImage] = useState(null); // état pour stocker l'index de l'image sélectionnée

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2, // nombre d'images affichées en même temps
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
  };

  return (
    <div className="projet">
      <h4>{titre}</h4>
      <h4>Technologies utilisées: {techno}</h4>
      <p>{description}</p>
      <Slider {...settings}>
        {imageSrc.map((imageSrc, index) => (
          <div key={index} onClick={() => setSelectedImage(index)}> // ajouter l'événement onClick pour mettre à jour l'état selectedImage
            <img src={imageSrc} alt={`${titre}-image-${index}`} />
          </div>
        ))}
      </Slider>
      <p>{link}</p>
      {selectedImage !== null && ( // afficher la modale si une image est sélectionnée
        <div className="modal" onClick={() => setSelectedImage(null)}>
          <img src={imageSrc[selectedImage]} alt={`${titre}-image-${selectedImage}`} />
        </div>
      )}
    </div>
  );
};

export default Projet;
