import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../Projet.css";

const Projet = ({ id, titre, techno, description, imageSrc, link }) => {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (index) => {
    setSelectedImage(index);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1
        }
      }
    ]
  };

  return (
    <div className="projet">
      <h4 id="rea_titre">{titre}</h4>
      <h4>Technologies utilis√©es: {techno}</h4>
      <div>{description}</div>
      <div className="carousel-container">
        <Slider {...settings} className="slider">
          {imageSrc.map((imageSrc, index) => (
            <img
              key={index}
              src={imageSrc}
              alt="Illustration du projet"
              onClick={() => handleImageClick(index)}
            />
          ))}
        </Slider>

      </div>
      <div>{link}</div>
      {selectedImage !== null && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <img src={imageSrc[selectedImage]} alt="Image agrandie" />
          </div>
        </div>
      )}
    </div>
  );
};

export default Projet;
